import { PayPalScriptProvider, usePayPalScriptReducer } from '@paypal/react-paypal-js'
import React, { useEffect } from 'react'
import { useLoading } from '../../hooks/useLoading';
import { pay } from '../../services/orderService';
import { useCart } from '../../hooks/useCart';
import { toast } from 'react-toastify';
import { useNavigate } from 'react-router-dom';

export default function PayPalButtons({ order }) {
  return (
    <PayPalScriptProvider
        options={{
            clientId: 'Aa_DY9XDQ4r8MQUuM7M0nQOID-ISGodlDNG8qqC4cqMoUz8HQTwCsw1DNiF-vohTLsH34T4ewRKa8e41'
        }}>
            <Buttons order={order} />
    </PayPalScriptProvider>
  )
}

function Buttons({order}){
    const { clearCart } = useCart();
    const navigate = useNavigate();
    const [{ isPending }] = usePayPalScriptReducer();
    const { showLoading, hideLoading } = useLoading();
    useEffect(() => {
        isPending? showLoading() : hideLoading();
    })

    const createOrder = (data, actions) => {
        return actions.order.create({
            purchase_units: [
                {
                    amount: {
                        currency_code: 'USD',
                        value: order.totalPrice,
                    },
                },
            ]
        })
    }

    const onApprove = async (data, actions) => {
        try {
            const payment = await actions.order.capture();
            const orderId = await pay(payment.id);
            clearCart();
            toast.success('Payment Saved Successfulyl', 'Success');
            navigate('/track/' + orderId);
        } catch (error) {
            toast.error('Payment Save Falied', 'Error');
        }
    }

    const onError = err => {
        toast.error('Payment Falied', 'Error');
    };

    return (
        <PayPalButtons
            createOrder={createOrder}
            onApprove={onApprove}
            onError={onError}
        />
    )
}
